mapper.select:
SELECT a.om_id, m.om_tel, m.om_company, a.ac_regdate
FROM meeting mt, application a, oim_member m
WHERE mt.meet_no = #{meet_no}
AND m.om_id=a.om_id
AND mt.om_id = #{om_hid}
ORDER BY ac_no ASC

SELECT meet_poster, meet_price, meet_start , meet_end,meet_total,meet_limit, meet_subject
FROM meeting
WHERE meet_no = #{meet_no}

mapper.update:
UPDATE meeting SET
meet_limit=meet_limit-1
WHERE meet_no=#{meet_no}

mapper.select:
SELECT m.meet_poster, m.meet_subject, m.meet_total, m.meet_limit, m.om_id, m.om_tel, m.om_company, a.ac_regdate
FROM meeting m, application a
where a.om_id = #{om_aid}
