mapper.select:
SELECT meet_poster, meet_price, meet_start , meet_end,meet_total,meet_limit, meet_subject,meet_regdate
FROM meeting
WHERE meet_no = #{meet_no}

mapper.update:
UPDATE meeting SET
meet_limit=meet_limit-1
WHERE meet_no=#{meet_no}

UPDATE meeting SET
meet_limit=meet_limit+1
WHERE meet_no=#{meet_no}
