mapper.update:
UPDATE omfreeboard SET
hit=hit+1
WHERE no=#{no}

mapper.select:
SELECT no,om_id,subject,content,regdate,hit
FROM omfreeboard
WHERE no=#{no}

mapper.update:
UPDATE omfreeboard SET
om_id=#{om_id},subject=#{subject},
content=#{content}
WHERE no=#{no}

mapper.select:
SELECT group_id,group_step,group_tab
FROM omreply
WHERE no=#{no}

mapper.update:
UPDATE omreply SET
group_step=group_step+1
WHERE group_id=#{group_id} AND group_step>#{group_step}

UPDATE omreply SET 
depth=depth+1
WHERE no=#{no}

UPDATE omreply SET
msg=#{msg}
WHERE no=#{no}

UPDATE omreply SET
msg='관리자가 삭제한 글입니다'
WHERE no=#{no}

UPDATE omreply SET
depth=depth-1
WHERE no=#{no}
