mapper.update:
UPDATE qaboard SET
qa_hit=qa_hit+1
WHERE qa_no=#{qa_no}

UPDATE qaboard SET
om_id=#{om_id},qa_subject=#{qa_subject},
qa_content=#{qa_content}
WHERE qa_no=#{qa_no}

mapper.select:
SELECT qa_group_id,qa_group_step,qa_group_tab
FROM qaboard
WHERE qa_no=#{qa_no}

mapper.update:
UPDATE qaboard SET
qa_group_step=qa_group_step+1
WHERE qa_group_id=#{qa_group_id} AND qa_group_step>#{qa_group_step}

UPDATE qaboard SET 
qa_depth=qa_depth+1
WHERE qa_no=#{qa_no}
